import React from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { UserProvider } from "./components/Usercontext";
import Signup from "./components/Signup";
import Login from "./components/Login";
import Dashboard from "./components/Dashboard";
import UpdatePass from "./components/UpdatePass";
import ResetPass from "./components/ResetPass";
import Topbar from "./components/sub-components/Topbar";
import Bottombar from "./components/sub-components/Bottombar";
import { useState } from "react";

function App() {
  const [comp, setComp] = useState("");
  const [link, setLink] = useState("");

  return (
    <Router>
      <UserProvider>
        <Topbar value={comp} link={link}></Topbar>
        <Switch>
          <Route exact={true} path="/login" component={Login}>
            {setComp("Login")} {setLink("/login")}
          </Route>
          <Route exact={true} path="/dashboard" component={Dashboard}></Route>
          <Route
            exact={true}
            path="/update-password/:randomstring"
            component={UpdatePass}
          ></Route>
          <Route
            exact={true}
            path="/reset-password"
            component={ResetPass}
          ></Route>
          <Route exact={true} path="/" component={Signup}></Route>
        </Switch>
        <Bottombar />
      </UserProvider>
    </Router>
  );
}

export default App;
