import { useState } from "react";
import { Helmet } from "react-helmet-async";
import Topbar from "./sub-components/Topbar";
import Bottombar from "./sub-components/Bottombar";

function Signup() {
  const [fullName, setFullName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [repeatPassword, setRepeatPassword] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const connect = await fetch(
      "https://node-mini-urls.herokuapp.com/sign-up",
      {
        method: "POST",
        body: JSON.stringify({ fullName, email, password }),
        headers: { "Content-Type": "application/json" },
      }
    );
    const res = await connect.json();
    handleErrors(res, connect);
    console.log(res);
  };

  const handleErrors = (res, connect) => {
    const alert = document.getElementById("alert");
    if (res.email === "User email already exists") {
      alert.innerText = "User email already exists";
      alert.classList.remove("hidden");
    }
    if (connect.status === 200) {
      alert.innerText = "User registration successful";
      alert.classList.remove("hidden");
    }
  };

  return (
    <>
      <Helmet>
        <title>Sign up / Mini Urls</title>
      </Helmet>
      <Topbar value="Login" link="/login"></Topbar>
      <div className="container">
        <div className="row">
          <div className="col-lg-6 form-container">
            <div className="bulb-img">
              <img src="/imgs/light-bulb.jpg" alt="light-bulb" />
            </div>
          </div>
          <div className="col-lg-6 form-container">
            <form
              className="row g-3 mx-auto p-4"
              style={{ maxWidth: "24rem" }}
              onSubmit={handleSubmit}
            >
              <h1 className="h2 p-2">Sign up</h1>

              <div
                class="alert alert-warning alert-dismissible fade show"
                role="alert"
              >
                <strong>Holy guacamole!</strong> You should check in on some of
                those fields below.
                <button
                  type="button"
                  class="close"
                  data-dismiss="alert"
                  aria-label="Close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>

              <div className="mb-3 col-md-6">
                <label htmlFor="inputFulltName" className="form-label">
                  Full Name
                </label>
                <input
                  type="text"
                  className="form-control"
                  id="inputFullName"
                  aria-describedby="fullName"
                  onChange={(e) => setFullName(e.target.value)}
                  value={fullName}
                  required
                />
              </div>
              <div className="mb-3 col-md-6">
                <label htmlFor="inputEmail1" className="form-label">
                  Email address
                </label>
                <input
                  type="email"
                  className="form-control"
                  id="inputEmail1"
                  aria-describedby="emailHelp"
                  onChange={(e) => setEmail(e.target.value)}
                  value={email}
                  required
                />
              </div>
              <div className="mb-3 col-md-6">
                <label htmlFor="inputPassword1" className="form-label">
                  Password
                </label>
                <input
                  type="password"
                  className="form-control"
                  id="inputPassword1"
                  onChange={(e) => setPassword(e.target.value)}
                  value={password}
                  minLength="6"
                  required
                />
              </div>
              <div className="mb-3 col-md-6">
                <label htmlFor="inputPassword2" className="form-label">
                  Repeat Password
                </label>
                <input
                  type="password"
                  className="form-control"
                  id="inputPassword2"
                  onChange={(e) => setRepeatPassword(e.target.value)}
                  value={repeatPassword}
                  minLength="6"
                  required
                />
              </div>
              <div id="emailHelp" className="form-text">
                We'll never share your information with anyone else.
              </div>
              <div className="mb-3 col-md-6 form-check">
                <input
                  type="checkbox"
                  className="form-check-input"
                  id="check"
                  required
                />
                <label className="form-check-label" htmlFor="check">
                  I agree to the T&C
                </label>
              </div>
              <div className="mb-3 col-md-6">
                <button type="submit" className="button btn btn-primary w-100">
                  Submit
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
      <Bottombar />
    </>
  );
}

export default Signup;
